import FormInput from "./common/FormInput"
import { useState } from "react"
import { useDispatch } from "react-redux";
import { useMutation, useQueryClient } from 'react-query';

export default function IMEISearch() {
    let dispatch = useDispatch();
    let [searchValue, setSearchValue] = useState('');
    let [error, setError] = useState('');

    const mutation = useMutation((newPost) =>
        fetch("/getPhoneCompatibility", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newPost),
        }).then(res => {
                return res.json()
            })
    );

    const submitData = () => {
        mutation.mutate({searchValue: searchValue});
    };

    if (mutation.isError) {
        setError(mutation.error.message);
    }

    if (mutation.isSuccess) {
        dispatch({
            type: 'SET_RESULT',
            result: mutation.data.plans
        });
    }

    function searchPlanByMobile() {
        const imeiRegex = /^[0-9]{15}$/;
        setError('');
        if (!imeiRegex.test(searchValue)) {
            setError('Please enter a valid 15-digit IMEI number');
        } else {
            // dispatch({
            //     type: 'SET_RESULT',
            //     result: []
            // });

            dispatch({
                type: 'SET_SEARCH_TYPE',
                searchType: 'IMEI'
            });

            dispatch({
                type: 'SET_SEARCH_VALUE',
                searchValue: searchValue
            });

            submitData();
        }
    }

    return (
        <div className="w-full">
            <FormInput searchType="number" placeholder="Enter 15-digit IMEI" 
                searchInput={searchValue} setSearchInput={setSearchValue}
                information="You can find your IMEI number by dialing *#06# on your phone's keypad or checking your device's settings." />
            {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
            <button disabled={mutation.isLoading} 
                className="w-full bg-blue-500 text-white p-2 mt-2 rounded hover:bg-blue-600 disabled:bg-gray-400" 
                onClick={searchPlanByMobile}>{mutation.isLoading ? 'Checking...' : 'Check Compatibility'}</button>
        </div>
    )

}
